package org.leetcode.Strings;

import org.junit.Test;

public class GreatestCommonDivisorofStrings_1071 {


  public String gcdOfStrings2(String str1, String str2) {

    if (str1.length() == 0 || str2.length() == 0) {
      return "";
    }

    if (str1.equals(str2)) {
      return str1;
    }

    int j = str2.length();

    while (j >= 0) {
      String cut = str2.substring(0, j);
      if (str1.replaceAll(cut, "").length() == 0 && str2.replaceAll(cut, "").length() == 0) {
        return cut;
      }
      j--;
    }

    return "";
  }


  @Test
  public void test1() {
    System.out.println(gcdOfStrings2("ABCABC", "ABC"));
    System.out.println(gcdOfStrings2("ABABAB", "ABAB"));
    System.out.println(gcdOfStrings2("LEET", "CODE"));
  }

  @Test
  public void test2() {
    System.out.println(gcdOfStrings2("TAUXXTAUXXTAUXXTAUXXTAUXX",
        "TAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXX"));
  }

  @Test
  public void test3() {
    System.out.println(gcdOfStrings2(
        "",
        ""));
  }

}
